# PowerShell Setup Script for AURORA Trading System\n\n# This script sets up the AURORA Trading System for development on Windows.\n# Usage: Run this script in PowerShell with administrative privileges.\n\n# Check for necessary prerequisites\n$prerequisites = @('Git', 'Node.js', 'npm')\n\nforeach ($prerequisite in $prerequisites) {\n    if (-not (Get-Command $prerequisite -ErrorAction SilentlyContinue)) {\n        Write-Host "$prerequisite is not installed. Please install it before proceeding." -ForegroundColor Red\n        exit 1\n    }\n}\n\n# Clone the repository\n$repoUrl = 'https://github.com/simonnmarket/AURORA-Trading-System.git'\n$localPath = 'C:\AURORA-Trading-System'\n\nif (-not (Test-Path $localPath)) {\n    Write-Host "Cloning the repository..."\n    git clone $repoUrl $localPath\n} else {\n    Write-Host "Repository already exists at $localPath. Pulling latest changes..."\n    cd $localPath\n    git pull\n}\n\n# Install Node.js dependencies\nWrite-Host "Installing Node.js dependencies..."\ncd $localPath\nnpm install\n\nWrite-Host "Setup completed. You can now start developing!"